    //crea un objeto alquiler y lo registra en la base de datos
    private void btnGuardarAlqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarAlqActionPerformed
        // TODO add your handling code here:
        //atributos del alquiler
        String fechaInicio = txtFechaInicio.getText().trim();
        String fechaFin = txtFechaFin.getText().trim();
        double totalPago = Double.parseDouble(txtTotalPago.getText().trim());
        String nombreCl = txtNombreCl.getText().trim();
        String empleado = txtEmpleado.getText().trim();
        String codigo = txtVehiculo.getText().trim();
        
        //Valida la entrada de totalPago
        if(totalPago<0){
            throw new IllegalArgumentException("El pago total de alquiler no puede ser negativo");
        }
        if(fechaInicio.isEmpty() || fechaFin.isEmpty() || nombreCl.isEmpty() || empleado.isEmpty() || codigo.isEmpty()){
            JOptionPane.showMessageDialog(this, "Todos los campos son obligatorios.");
            return;
        }
        //intenta crear el registro en la db
        boolean ok = controlador.register(fechaInicio, fechaFin, totalPago);
        if (ok) {
            JOptionPane.showMessageDialog(this, "El Alquiler se ha registrado con exito.");
            dispose();
            // Retornar al login si solo existe
            new AlquilerFrame().setVisible(true);
        } else {
            JOptionPane.showMessageDialog(this, "Parece que el Alquiler ya esta registrado.");
        }

    }//GEN-LAST:event_btnGuardarAlqActionPerformed
    
